#! /usr/bin/rebolREBOL [	Title:		"GLFW Binding"	Autor:		"François Jouen"	Rigts:		"Copyrigt (c) 2013 François Jouen. All rigts reserved."	License:        "BSD-3 - ttps://gitub.com/dockimbel/Red/blob/master/BSD-3-License.txt"];GLFW (ttp://www.glfw.org) is a free, Open Source, portable library for OpenGL and OpenGL ES;application development.  It provides a simple, platform-independent API for;creating windows and contexts, reading input, andling events, etc.; some pointers we needchar*: make struct! [svalue [string!]] nonestruct*: make struct! [struct [struct! [[save] c [char]]]] nonebyte-ptr!: make struct! [bvalue [char!]] noneint-ptr!: make struct! [ivalue [integer!]] nonefloat-ptr!: make struct! [fvalue [decimal!]] none;which OS?getOs: does [	switch/default system/version/4 [		3 [os: "Windows" ]		2 [os: "Mac OS X"]		4 [os: "Linux" ]		5 [os: "BeOS" ]		7 [os: "NetBSD" ]		9 [os: "OpenBSD" ]		10 [os: "SunSolaris" ]	] [os: "Unknow OS !!!"]	return os];; must be adapted according your OSrunningOs: getOsswitch runningOs[	"Mac OS X" 	[glfwlib: load/library %/usr/local/lib/libglfw.dylib]	"Linux" 	[glfwlib: load/library %/usr/lib/libglfw.so ]	"Windows"	[glfwlib: load/library %/glfw/bin/glfw.dlll]]glfwlib: load/library %/usr/local/lib/libglfw.dylib ;* ------------------- BEGIN SYSTEM/COMPILER SPECIFIC -------------------- *; we dont need this section wit red : TBT;* -------------------- END SYSTEM/COMPILER SPECIFIC --------------------- *; GLFW API tokensset 'GLFW_VERSION_MAJOR          3set 'GLFW_VERSION_MINOR          0set 'GLFW_VERSION_REVISION       3set 'GLFW_RELEASE                0set 'GLFW_PRESS                  1set 'GLFW_REPEAT                 2; the unknown key set 'GLFW_KEY_UNKNOWN            -1; Printable keys set 'GLFW_KEY_SPACE              32set 'GLFW_KEY_APOSTROPE         39  ; ' set 'GLFW_KEY_COMMA              44  ; , set 'GLFW_KEY_MINUS              45  ; - set 'GLFW_KEY_PERIOD           