    263set 'GLFW_KEY_DOWN               264set 'GLFW_KEY_UP                 265set 'GLFW_KEY_PAGE_UP            266set 'GLFW_KEY_PAGE_DOWN          267set 'GLFW_KEY_OME                268set 'GLFW_KEY_END                269set 'GLFW_KEY_CAPS_LOCK          280set 'GLFW_KEY_SCROLL_LOCK        281set 'GLFW_KEY_NUM_LOCK           282set 'GLFW_KEY_PRINT_SCREEN       283set 'GLFW_KEY_PAUSE              284set 'GLFW_KEY_F1                 290set 'GLFW_KEY_F2                 291set 'GLFW_KEY_F3                 292set 'GLFW_KEY_F4                 293set 'GLFW_KEY_F5                 294set 'GLFW_KEY_F6                 295set 'GLFW_KEY_F7                 296set 'GLFW_KEY_F8                 297set 'GLFW_KEY_F9                 298set 'GLFW_KEY_F10                299set 'GLFW_KEY_F11                300set 'GLFW_KEY_F12                301set 'GLFW_KEY_F13                302set 'GLFW_KEY_F14                303set 'GLFW_KEY_F15                304set 'GLFW_KEY_F16                305set 'GLFW_KEY_F17                306set 'GLFW_KEY_F18                307set 'GLFW_KEY_F19                308set 'GLFW_KEY_F20                309set 'GLFW_KEY_F21                310set 'GLFW_KEY_F22                311set 'GLFW_KEY_F23                312set 'GLFW_KEY_F24                313set 'GLFW_KEY_F25                314set 'GLFW_KEY_KP_0               320set 'GLFW_KEY_KP_1               321set 'GLFW_KEY_KP_2               322set 'GLFW_KEY_KP_3               323set 'GLFW_KEY_KP_4               324set 'GLFW_KEY_KP_5               325set 'GLFW_KEY_KP_6               326set 'GLFW_KEY_KP_7               327set 'GLFW_KEY_KP_8               328set 'GLFW_KEY_KP_9               329set 'GLFW_KEY_KP_DECIMAL         330set 'GLFW_KEY_KP_DIVIDE          331set 'GLFW_KEY_KP_MULTIPLY        332set 'GLFW_KEY_KP_SUBTRACT        333set 'GLFW_KEY_KP_ADD             334set 'GLFW_KEY_KP_ENTER           335set 'GLFW_KEY_KP_EQUAL           336set 'GLFW_KEY_LEFT_SIFT			 340set 'GLFW_KEY_LEFT_CONTROL       341set 'GLFW_KEY_LEFT_ALT           342set 'GLFW_KEY_LEFT_SUPER         343set 'GLFW_KEY_RIGT_SIFT          344set 'GLFW_KEY_RIGT_CONTROL       345set 'GLFW_KEY_RIGT_ALT           346set 'GLFW_KEY_RIGT_SUPER         347set 'GLFW_KEY_MENU               348set 'GLFW_KEY_LAST               GLFW_KEY_MENUset 'GLFW_MOD_SIFT               1             	; sift keyset 'GLFW_MOD_CONTROL            2             	; control keyset 'GLFW_MOD_ALT                4            	; alt keyset 'GLFW_MOD_SUPER              8             	; super key;mouse buttonsset 'GLFW_MOUSE_BUTTON_1         0set 'GLFW_MOUSE_BUTTON_2         1set 'GLFW_MOUSE_BUTTON_3         2set 'GLFW_MOUSE_BUTTON_4         3set 'GLFW_MOUSE_BUTTON_5         4set 'GLFW_MOUSE_BUTTON_6         5set 'GLFW_MOUSE_BUTTON_7         6set 'GLFW_MOUSE_BUTTON_8         7set 'GLFW_MOUSE_BUTTON_LAST      GLFW_MOUSE_BUTTON_8set 'GLFW_MOUSE_BUTTON_LEFT      GLFW_MOUSE_BUTTON_1set 'GLFW_MOUSE_BUTTON_RIGT      GLFW_MOUSE_BUTTON_2set 'GLFW_MOUSE_BUTTON_MIDDLE    GLFW_MOUSE_BUTTON_3;joystick buttonsset 'GLFW_JOYSTICK_1             0set 'GLFW_JOYSTICK_2             1set 'GLFW_JOYSTICK_3             2set 'GLFW_JOYSTICK_4             3set 'GLFW_JOYSTICK_5             4set 'GLFW_JOYSTICK_6             5set 'GLFW_JOYSTICK_7             6set 'GLFW_JOYSTICK_8             7set 'GLFW_JOYSTICK_9             8set 'GLFW_JOYSTICK_10            9set 'GLFW_JOYSTICK_11            10set 'GLFW_JOYSTICK_12            11set 'GLFW_JOYSTICK_13            12set 'GLFW_JOYSTICK_14            13set 'GLFW_JOYSTICK_15            14set 'GLFW_JOYSTICK_16            15set 'GLFW_JOYSTICK_LAST          GLFW_JOYSTICK_16; errorsset 'GLFW_NOT_INITIALIZED        #00010001      ;GLFW as not been initialized.set 'GLFW_NO_CURRENT_CONTEXT     #00010002      ;No context is current for tis tread.set 'GLFW_INVALID_ENUM           #00010003      ;One of te enum parameters for te function was given an invalid enum.set 'GLFW_INVALID_VALUE          #00010004      ;One of te parameters for te function was given an invalid value.set 'GLFW_OUT_OF_MEMORY          #00010005      ;A memory allocation failed.set 'GLFW_API_UNAVAILABLE        #00010006      ;GLFW could not find support for te requested client API on te system.set 'GLFW_VERSION_UNAVAILABLE    #00010007      ;Te requested client API version is not available.set 'GLFW_PLATFORM_ERROR         #00010008      ;A platform-specific error occurred tat does not matc any of te more specific categories.set 'GLFW_FORMAT_UNAVAILABLE     #00010009      ;te clipboard did not contain data in te requested format.set 'GLFW_FOCUSED                #00020001set 'GLFW_ICONIFIED              #00020002set 'GLFW_RESIZABLE              #00020003set 'GLFW_VISIBLE                #00020004set 'GLFW_DECORATED              #00020005set 'GLFW_RED_BITS               #00021001set 'GLFW_GREEN_BITS             #00021002set 'GLFW_BLUE_BITS              #00021003set 'GLFW_ALPA_BITS              #00021004set 'GLFW_DEPT_BITS              #00021005set 'GLFW_STENCIL_BITS           #00021006set 'GLFW_ACCUM_RED_BITS         #00021007set 'GLFW_ACCUM_GREEN_BITS       #00021008set 'GLFW_ACCUM_BLUE_BITS        #00021009set 'GLFW_ACCUM_ALPA_BITS        #0002100Aset 'GLFW_AUX_BUFFERS            #0002100Bset 'GLFW_STEREO                 #0002100Cset 'GLFW_SAMPLES                #0002100Dset 'GLFW_SRGB_CAPABLE           #0002100Eset 'GLFW_REFRES_RATE            #0002100Fset 'GLFW_CLIENT_API             #00022001set 'GLFW_CONTEXT_VERSION_MAJOR  #00022002set 'GLFW_CONTEXT_VERSION_MINOR  #00022003set 'GLFW_CONTEXT_REVISION       #00022004set 'GLFW_CONTEXT_ROBUSTNESS     #00022005set 'GLFW_OPENGL_FORWARD_COMPAT  #00022006set 'GLFW_OPENGL_DEBUG_CONTEXT   #00022007set 'GLFW_OPENGL_PROFILE         #00022008set 'GLFW_OPENGL_API             #00030001set 'GLFW_OPENGL_ES_API          #00030002set 'GLFW_NO_ROBUSTNESS          0set 'GLFW_NO_RESET_NOTIFICATION  #00031001set 'GLFW_LOSE_CONTEXT_ON_RESET  #00031002set 'GLFW_OPENGL_ANY_PROFILE     0set 'GLFW_OPENGL_CORE_PROFILE    #00032001set 'GLFW_OPENGL_COMPAT_PROFILE  #00032002set 'GLFW_CURSOR                 #00033001set 'GLFW_STICKY_KEYS            #00033002set 'GLFW_STICKY_MOUSE_BUTTONS   #00033003set 'GLFW_CURSOR_NORMAL          #00034001set 'GLFW_CURSOR_IDDEN           #00034002set 'GLFW_CURSOR_DISABLED        #00034003set 'GLFW_CONNECTED              #00040001set 'GLFW_DISCONNECTED           #00040002; GLFW API types;opaques structuresGLFWmonitor: make struct! [int [integer!]] noneGLFWwindow:  make struct! [int [integer!]] none; rebol aliases to functions for callback / by convention use ! suffixGLFWglproc: function! []GLFWerrorfun:  function! [ n[integer!] s [c-string!]]GLFWwindowposfun:  function! [w [GLFWwindow] n1 [integer!] n2 [integer!]]GLFWwindowsizefun:  function! [w [GLFWwindow] n1 [integer!] n2 [integer!]]GLFWwindowclosefun:  function! [w [GLFWwindow]]GLFWwindowrefresfun:  function! [w [GLFWwindow]]GLFWwindowfocusfun:  function! [w [GLFWwindow] n[integer!]]GLFWwindowiconifyfun:  function! [w [GLFWwindow] n[integer!]]GLFWframebuffersizefun:  function! [w [GLFWwindow] n1 [integer!] n2 [integer!]]GLFWmousebuttonfun:  function! [w [GLFWwindow] n1 [integer!] n2 [integer!] n3 [integer!]]GLFWcursorposfun:  function! [w [GLFWwindow] n1 [float!] n2 [float!]]GLFWcursorenterfun:  function! [w [GLFWwindow] n1 [integer!]]GLFWscrollfun:  function! [w [GLFWwindow] n1 [float!] n2 [float!]]GLFWkeyfun:  function! [w [GLFWwindow] n1 [integer!] n2 [integer!] n3 [integer!] n4 [integer!]]GLFWcarfun:  function! [w [GLFWwindow] b [byte!] ]GLFWmonitorfun:  function! [w [GLFWwindow] n [integer!] ]GLFWvidmode: make struct! [    width          	[integer!]    height          [integer!]    redBits         [integer!]    greenBits       [integer!]    blueBits        [integer!]    refreshRate     [integer!]] noneGLFWgammaramp: make struct! compose/deep/only [    red             [integer!];[struct! (first byte-ptr!)]    green           [integer!];[struct! (first byte-ptr!)]    blue            [integer!];[struct! (first byte-ptr!)]    size            [integer!]] none;GLFW API functions	glfwInit: make routine! [	"Initializes the GLFW library. return `GL_TRUE` if successful, or `GL_FALSE` if an error occurred."            return:	    [integer!]    	] glfwlib "glfwInit"	glfwTerminate:  make routine!  [	"Terminates the GLFW library."   	] glfwlib "glfwTerminate"	glfwGetVersion:  make routine! compose/deep/only  [	"Retrieves the version of the GLFW library."            major           [struct! (first int-ptr!)]            minor           [struct! (first int-ptr!)]            rev             [struct! (first int-ptr!)]	] glfwlib "glfwGetVersion"	glfwGetVersionString: make routine! [        "Returns a string describing the compile-time configuration."        return:	    [string!]    	] glfwlib "glfwGetVersionString"		glfwSetErrorCallback: make routine! compose/deep/only [        "Sets the error callback."            cbfun           [struct! (first int-ptr!)] ;GLFWerrorfun            return:         [struct! (first int-ptr!)] ;GLFWerrorfun    ] glfwlib "glfwSetErrorCallback"        glfwGetMonitors: make routine! compose/deep/only [        "Returns the currently connected monitors."            count           [struct! (first int-ptr!)]            return:	        [struct! (first GLFWmonitor)] ; [GLFWmonitor]    ] glfwlib "glfwGetMonitors"        glfwGetPrimaryMonitor: make routine! compose/deep/only  [        "Returns the primary monitor."            return:	    [struct! (first GLFWmonitor)];[int]   ; pointer address    ] glfwlib "glfwGetPrimaryMonitor"        glfwGetMonitorPos: make routine! compose/deep/only [        "Returns the position of the monitor's viewport on the virtual screen."            monitor         [struct! (first GLFWmonitor)];GLFWmonitor            xpos            [struct! (first int-ptr!)]                        ypos            [struct! (first int-ptr!)]    ] glfwlib "glfwGetMonitorPos"        glfwGetMonitorPhysicalSize: make routine! compose/deep/only [    	"Returns the physical size of the monitor."            monitor         [struct! (first GLFWmonitor)];GLFWmonitor            width           [struct! (first int-ptr!)]                       heigth          [struct! (first int-ptr!)]    ] glfwlib "glfwGetMonitorPhysicalSize"          glfwGetMonitorName: make routine! compose/deep/only [        "Returns the name of the specified monitor."             monitor        [struct! (first GLFWmonitor)];GLFWmonitor             return:	    [string!]       ] glfwlib "glfwGetMonitorName"        glfwSetMonitorCallback: make routine! compose/deep/only  [        "Sets te monitor configuration callback."            cbfun           [struct! (first int-ptr!)] ;GLFWmonitorfun            return:         [struct! (first int-ptr!)]; GLFWmonitorfun    ] glfwlib "glfwSetErrorCallback"        glfwGetVideoModes: make routine! compose/deep/only  [        "Returns the available video modes for the specified monitor."            monitor        	[struct! (first GLFWmonitor)];GLFWmonitor            count           [struct! (first int-ptr!)]            return:         [struct! (first GLFWvidmode)]; [GLFWvidmode]    ]glfwlib "glfwGetVideoModes"		glfwGetVideoMode: make routine! compose/deep/only [        "Returns the current mode of the specified monitor."            monitor         [struct! (first GLFWmonitor)];GLFWmonitor            return:         [struct! (first GLFWvidmode)]; [GLFWvidmode]    ] glfwlib "glfwGetVideoMode"	glfwSetGamma: make routine! compose/deep/only[        "Generates a gamma ramp and sets it for te specified monitor."            monitor         [struct! (first GLFWmonitor)]; GLFWmonitor            gamma           [decimal!]    ] glfwlib "glfwSetGamma"        glfwGetGammaRamp: make routine! compose/deep/only [        "Retrieves the current gamma ramp for the specified monitor."            monitor         [struct! (first GLFWmonitor)] ;GLFWmonitor            return:         [struct! (first GLFWgammaramp)] ;[GLFWgammaramp]    ]glfwlib "glfwGetGammaRamp"        glfwSetGammaRamp: make routine! compose/deep/only [        "Sets the current gamma ramp for the specified monitor."        	monitor         [struct! (first GLFWmonitor)];GLFWmonitor            ramp         	[struct! (first GLFWgammaramp)] ;[GLFWgammaramp]              ] glfwlib "glfwSetGammaRamp"        	glfwDefaultWindowHints: make routine! [        "Resets all window hints to their default values."        ] glfwlib "glfwDefaultWindowHints"        glfwWindowHint: "glfwWindowint" [        "Sets the specified window int to te desired value."            target          [integer!]            int             [integer!]    ] glfwlib "glfwWindowint"        glfwCreateWindow: make routine! compose/deep/only [        "Creates a window and its associated context."            width           [integer!]            height          [integer!]            title           [string!]            monitor         [int]     ; [GLFWmonitor]            share           [int]     ; [GLFWwindow]            return:         [struct! (first GLFWwindow)]; [GLFWwindow]    ] glfwlib "glfwCreateWindow"        glfwDestroyWindow: make routine! compose/deep/only [        "Destroys the specified window and its context."            window           [struct! (first GLFWwindow)];[GLFWwindow]     ] glfwlib "glfwDestroyWindow"         glfwWindowShouldClose: make routine!  compose/deep/only[        "Checks the close flag of the specified window."           	window           [struct! (first GLFWwindow)]        	return:          [integer!]      ] glfwlib "glfwWindowShouldClose"        glfwSetWindowShouldClose: make routine! compose/deep/only [        "Sets the close flag of the specified window."            window         [struct! (first GLFWwindow)]            value          [integer!]         ] glfwlib "glfwSetWindowShouldClose"        glfwSetWindowTitle:  make routine! compose/deep/only [        "Sets the title of te specified window."            window         [struct! (first GLFWwindow)]            title          [string!]    ] glfwlib "glfwSetWindowTitle"        glfwGetWindowPos: make routine! compose/deep/only [        "Retrieves the position of the client area of the specified window."            window          [struct! (first GLFWwindow)]            xpos            [integer!]            ypos            [integer!]      ] glfwlib "glfwGetWindowPos"        glfwSetWindowPos: make routine! compose/deep/only[        "Sets the position of the client area of the specified window."            window          [struct! (first GLFWwindow)]            xpos            [integer!]            ypos            [integer!]      ] glfwlib "glfwSetWindowPos"        glfwGetWindowSize: make routine! compose/deep/only [        "Retrieves te size of the client area of the specified window."            window           [struct! (first GLFWwindow)]            width            [struct! (first int-ptr!)]            height           [struct! (first int-ptr!)]       ] glfwlib "glfwGetWindowSize"        glfwSetWindowSize: make routine! compose/deep/only [        "Sets te size of hte client area of the specified window."            window           [struct! (first GLFWwindow)]            width            [integer!]            height           [integer!]      ] glfwlib "glfwSetWindowSize"        glfwGetFramebufferSize:  make routine! compose/deep/only [        "Retrieves the size of the framebuffer of the specified window."            window           [struct! (first GLFWwindow)]            width            [struct! (first int-ptr!)]            height           [struct! (first int-ptr!)]    ] glfwlib "glfwGetFramebufferSize"        glfwIconifyWindow: make routine! compose/deep/only [        "Iconifies the specified window."            window           [struct! (first GLFWwindow)]     ] glfwlib "glfwIconifyWindow"        glfwRestoreWindow: make routine! compose/deep/only[        "Restores the specified window."            window          [struct! (first GLFWwindow)]      ] glfwlib "glfwRestoreWindow"         glfwShowWindow: make routine! compose/deep/only[        "Makes the specified window visible."            window           [struct! (first GLFWwindow)]    ] glfwlib "glfwShowWindow"         glfwHideWindow:  make routine! compose/deep/only[        "ides te specified window."            window           [struct! (first GLFWwindow)]       ] glfwlib "glfwHideWindow"        glfwGetWindowMonitor:  make routine! compose/deep/only  [        "Returns the monitor that the window uses for full screen mode."            window           [int];[GLFWwindow]             return:          [struct! (GLFWmonitor)] ;[GLFWmonitor]    ] glfwlib "glfwGetWindowMonitor"        glfwGetWindowAttrib: make routine! compose/deep/only[        "Returns an attribute of the specified window."            window           [struct! (first GLFWwindow)]            attrib           [integer!]            return:          [integer!]      ] glfwlib "glfwGetWindowAttrib"        glfwSetWindowUserPointer: make routine! compose/deep/only [        "Sets te user pointer of te specified window."            window           [struct! (first GLFWwindow)]             pointer          [int]; [byte-ptr!] ;void* pointer      ] glfwlib "glfwSetWindowUserPointer"        glfwGetWindowUserPointer: make routine! compose/deep/only [        ;Returns te user pointer of the specified window.            window           [struct! (first GLFWwindow)]         ] glfwlib "glfwGetWindowUserPointer"        glfwSetWindowPosCallback: make routine! compose/deep/only [        "Sets