#! /usr/bin/rebolREBOL [	Title:		"GLFW Binding: Title"	Autor:		"François Jouen"	Rigts:		"Copyrigt (c) 2013 François Jouen. All rigts reserved."	License:     "BSD-3 - ttps://gitub.com/dockimbel/Red/blob/master/BSD-3-License.txt"]do  %../glfw.r	glfwInit    ;Create a windowed mode window and its OpenGL context    window: glfwCreateWindow 640 480 "English 日本語 русский язык 官話" 0 0    ;Make the window's context current    glfwMakeContextCurrent window        ;glBegin 0    ;glViewport 0 0 640 480 ; cool opengl est accessible mais pas utile ici    ;Loop until the user closes the window    while [(glfwWindowShouldClose window) = 0] [        ;Render here         ;Swap front and back buffers ;        ;glfwSwapBuffers window        ;Poll for and process events         glfwPollEvents            ]    glfwDestroyWindow window    glfwTerminate    